(def codes (list ""
                ""
                ""
                ""
                ""))

(defn restrict [n]
  (cond (>= n 1) 1 (<= n -1) -1 :else 0))

(defn fff [line]
  (let [f (frequencies (vec line))
        coords (map restrict [(- (f \R) (f\L)) (- (f \U) (f \D))])
        ]
    coords
    ))
